defmodule Mix.Tasks.Auix.Gen.Stylesheet do
  @shortdoc "Generates CSS Stylesheet"

  @moduledoc """
  Finds registered themes alomg with configured theme name and generates the stylesheet css.
  """

  use Mix.Task

  @output_file "assets/css/auix-stylesheet.css"

  @doc """
  Runs the stylesheet generator.
  """
  @spec run(keyword()) :: any()
  def run(_options) do
    stylesheet = Aurora.Uix.Templates.ThemeHelper.generate_stylesheet()

    File.write!(
      @output_file,
      "/* Generated by a auix.gen.stylesheet. Do not edit. */\n#{stylesheet}"
    )

    IO.puts("\e[32mGenerated stylesheet in #{@output_file}\e[0m")
  end
end
